const path = require('path')

const StatCollector = require('./stat-collector')


module.exports = class SizeCollector extends StatCollector {
  constructor() {
    super('size')
  }

  async collect(pathStr, stat) {
    if (!stat) stat = await fs.stat(pathStr)
    if (stat.isFile()) return stat.size
    if (!stat.isDirectory()) return 0

    const children = await fs.readdir(pathStr)
    return children.reduce(async (accum, child) => {
      const size = await this.collect(path.join(pathStr, child), null)
      return (await accum) + size
    }, 0)
  }
}

/**
 * Statistics collector
 * @abstract
 */
class StatCollector {
  /**
   * Constructor
   * @param {string} name - Name of the collector and property key when exporting staticstics
   */
  constructor(name) {
    /**
     * @private
     * @type {string}
     */
    this.name = name
  }

  /**
   * Get the name of the collector
   * @return {string} Name of the collector
   */
  getName() {
    return this.name
  }

  /**
   * Collect statistics of an entity like the directory itself or its children
   * @abstract
   * @param {string} path - Path to the entity
   * @param {fs.Stats} stat - Basic statistics of the entity
   */
  async collect(path, stat) {
    // need overriding
  }
}

module.exports = StatCollector

const StatCollector = require('./stat-collector')


module.exports = class TimestampCollector extends StatCollector {
  constructor() {
    super('timestamp')
  }

  async collect(path, stat) {
    return {
      atime: stat.atime,
      birthtime: stat.birthtime,
      ctime: stat.ctime,
      mtime: stat.mtime,
    }
  }
}

const StatCollector = require('./stat-collector')


module.exports = class TypeCollector extends StatCollector {
  constructor() {
    super('type')
  }

  async collect(path, stat) {
    if (stat.isBlockDevice()) return 'block device'
    else if (stat.isCharacterDevice()) return 'character device'
    else if (stat.isDirectory()) return 'directory'
    else if (stat.isFIFO()) return 'fifo pipe'
    else if (stat.isFile()) return 'file'
    else if (stat.isSocket()) return 'socket'
    else if (stat.isSymbolicLink()) return 'symbolic link'
    return undefined
  }
}

const fsModule = require('fs')
let fs = fsModule.promises
if (!fs) {
  const { promisify } = require('util')
  fs = {
    stat: promisify(fsModule.stat),
    writeFile: promisify(fsModule.writeFile),
  }
}
const path = require('path')
const fg = require('fast-glob')

const statCollectors = require('./stat-collectors')


/**
 * Statistics writer
 */
class StatWriter {
  /**
   * Constructor
   * @param {string} dir - Path to a directory
   * @param {Object} options - Options
   */
  constructor(dir, options) {
    /**
     * @private
     * @type {string}
     */
    this.dir = dir
    /**
     * @private
     * @type {Object}
     */
    this.options = Object.assign({
      minified: false,
      output: '.dirstat',
      exclude: [],
      recursive: true,
      statCollectors: [
        new statCollectors.SizeCollector(),
        new statCollectors.TimestampCollector(),
        new statCollectors.TypeCollector()
      ]
    }, options)
    if (this.options.depth === undefined) this.options.depth = this.options.recursive ? -1 : 0
    if (this.options.depth < 0) this.options.depth = Infinity
    /**
     * @private
     * @type {Array<StatCollector>}
     */
    this.statCollectors = [new statCollectors.PathCollector(), ...this.options.statCollectors]
    /**
     * @private
     * @type {Object}
     */
    this.stat = {}
  }

  /**
   * Get a directory's children statistics
   * @private
   * @param {string} pathStr - Path to a directory
   * @param {number} depth - Depth
   * @return {Promise<Array<Object>>} Directory's children statistics
   */
  async getStatChildren(pathStr, depth) {
    const children = await fg('*', {
      cwd: pathStr,
      deep: 0,
      dot: true,
      followSymlinkedDirectories: false,
      ignore: this.options.exclude,
      onlyFiles: false,
    })
    const result = children.map(async (child) => await this.getStat(path.join(pathStr, child), depth))
    return Promise.all(result)
  }

  /**
   * Get a directory statistics
   * @private
   * @param {string} pathStr - Path to a directory
   * @param {number} depth - Depth
   * @return {Promise<Object>} Directory statistics
   */
  async getStat(pathStr, depth) {
    const result = {}
    const stat = await fs.stat(pathStr)
    await Promise.all(
      this.statCollectors.map(async (collector) => result[collector.getName()] = await collector.collect(pathStr, stat))
      )
    if (depth > 0 && stat.isDirectory()) result.children = await this.getStatChildren(pathStr, depth - 1)
    return result
  }

  /**
   * Get a directory statistics and write to a file
   */
  async export() {
    try {
      this.stat = await this.getStat(this.dir, this.options.depth)
      await fs.writeFile(
        path.join(this.dir, this.options.output),
        JSON.stringify(this.stat, null, this.options.minified ? 0 : 2)
      )
    } catch (err) {
      throw err
    }
  }
}

module.exports = StatWriter

